interface IVerifiedDomainRegistry {

    struct Domain {
        address owner;
        string name;
        uint256 expiryTime;
        address exchange;
        uint256 listingId;
    }

    struct DomainWithId {
        bytes32 domainId;
        Domain domain;
    }

